      enumValues(includeDeprecated: true) {
        name
        ").concat(descriptions, "
        isDeprecated
        deprecationReason
      }
      possibleTypes {
        ...TypeRef
      }
    }

    fragment InputValue on __InputValue {
      name
      ").concat(descriptions, "
